import { useState, useEffect } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-routing-machine';

function PhoneMap({ phoneData, startLocation, endLocation }) {
  const [routingControl, setRoutingControl] = useState(null);

  useEffect(() => {
    const map = L.map('map').setView([0, 0], 2);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Map data &copy; OpenStreetMap contributors'
    }).addTo(map);

    const control = L.Routing.control({
      waypoints: [
        L.latLng(startLocation.latitude, startLocation.longitude),
        L.latLng(endLocation.latitude, endLocation.longitude)
      ],
      routeWhileDragging: true
    }).addTo(map);

    setRoutingControl(control);

    phoneData.forEach(item => {
      const marker = L.marker([item.latitude, item.longitude]).addTo(map);
      marker.bindPopup(`User: ${item.capturedBy}, Capture date: ${item.captureDate}`);
    });

    return () => {
      map.remove();
    };
  }, [phoneData, startLocation, endLocation]);

  useEffect(() => {
    return () => {
      if (routingControl) {
        routingControl.removeFrom(routingControl._map);
      }
    };
  }, [routingControl]);

  return <div id="map" style={{ height: '400px' }}></div>;
}

export default PhoneMap;
