import React, { useContext, useState, useEffect } from 'react';
import { Navigate } from 'react-router';
import { UserContext } from '../userContext';

function AddPhoneData() {
    const userContext = useContext(UserContext); 
    const[latitude, setLatitude] = useState('');
    const[longitude, setLongitude] = useState('');
    const[uploaded, setUploaded] = useState(false);

    // const onSubmit = async (e) => {
    //     e.preventDefault();
    //     try {
    //       await fetch('http://localhost:3001/phoneData', {
    //         method: 'POST',
    //         credentials: 'include',
    //         headers: { 'Content-Type': 'application/json' },
    //         body: JSON.stringify({ latitude, longitude })
    //       });
    //       setLatitude('');
    //       setLongitude('');
    //     } catch (error) {
    //       console.error('Error adding phone data:', error);
    //     }
    //   };

    async function onSubmit(e) {
        e.preventDefault();
      
        // Check if latitude and longitude are defined
        if (latitude && longitude) {
            console.log("arg" + latitude);
            console.log("hah" + longitude);

          const formData = new FormData();
          formData.append('latitude', latitude);
          formData.append('longitude', longitude);

            console.log(formData);

          const res = await fetch('http://localhost:3001/phoneData', {
            method: 'POST',
            credentials: 'include',
            headers: { 'Content-Type': 'application/json' },
            body: formData
          });
          const data = await res.json();
      
          setUploaded(true);
        } else {
          // Handle case when latitude or longitude is not defined
          console.log('Latitude or longitude is not defined');
          // You can display an error message or perform any necessary actions here
        }
      }

    return (
         <form className="form-group" onSubmit={onSubmit}>
            {!userContext.user ? <Navigate replace to="/login" /> : ""}
            {uploaded ? <Navigate replace to="/phoneData" /> : ""}
            <label>
            Latitude:
            <input type="number" name="latitude" className="form-control" value={latitude} onChange={(e) => setLatitude(e.target.value)} required/>
            </label>
            <br />
            <label>
            Longitude:
            <input type="number" name="longitude" className="form-control" value={longitude} onChange={(e) => setLongitude(e.target.value)} required/>
            </label>
            <br /><br />
            <input className="btn btn-primary" type="submit" name="submit" value="Add Phone Data" />
       </form>
    )
}

export default AddPhoneData;
