import React, { useContext, useState, useEffect } from 'react';
import { Navigate } from 'react-router';
import { UserContext } from '../userContext';

function AddPhoneData() {
    const userContext = useContext(UserContext); 
    const[latitude, setLatitude] = useState('');
    const[longitude, setLongitude] = useState('');
    const[uploaded, setUploaded] = useState(false);

    async function onSubmit(e){
        e.preventDefault();

        const formData = new FormData();
        formData.append('latitude', latitude);
        formData.append('longitude', longitude);
        const res = await fetch('http://localhost:3001/phoneData', {
            method: 'POST',
            credentials: 'include',
            body: formData
        });
        const data = await res.json();

        setUploaded(true);
    }

    return (
        // <form className="form-group" onSubmit={onSubmit}>
        //     {!userContext.user ? <Navigate replace to="/login" /> : ""}
        //     {uploaded ? <Navigate replace to="/" /> : ""}
        //     <input type="text" className="form-control" name="ime" placeholder="Ime slike" value={name} onChange={(e)=>{setName(e.target.value)}}/>
        //     <label>Izberi sliko</label>
        //     <input type="file" id="file" onChange={(e)=>{setFile(e.target.files[0])}}/>
        //     <input className="btn btn-primary" type="submit" name="submit" value="Naloži" />
        // </form>
         <form className="form-group" onSubmit={onSubmit}>
            {!userContext.user ? <Navigate replace to="/login" /> : ""}
            {uploaded ? <Navigate replace to="/phoneData" /> : ""}
            <label>
            Latitude:
            <input type="number" className="form-control" value={latitude} onChange={(e) => setLatitude(e.target.value)} required/>
            </label>
            <br />
            <label>
            Longitude:
            <input type="number" className="form-control" value={longitude} onChange={(e) => setLongitude(e.target.value)} required/>
            </label>
            <br /><br />
            <input className="btn btn-primary" type="submit" name="submit" value="Add Phone Data" />
       </form>
    )
}

export default AddPhoneData;
