import { useState, useEffect } from 'react';
import React, { useRef } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-routing-machine';
import markerIcon from './marker.png'; 

function PhoneMap({ phoneData, startLocation, middleLocation, endLocation }) {
  const mapRef = useRef(null);
  const routingControlRef = useRef(null);
  const routingControlRefs = useRef([]); //useRef(null);

  useEffect(() => {
    const customIcon = L.icon({
      iconUrl: markerIcon,
      iconSize: [25, 41], 
      iconAnchor: [12, 41], 
      popupAnchor: [0, -41] 
    });

    const map = L.map(mapRef.current, {
      fullscreenControl: true
    }).setView([0, 0], 3);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution:
        'Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors',
      maxZoom: 18
    }).addTo(map);


    phoneData.forEach(item => {
      const routingControl = L.Routing.control({
        waypoints: [
          L.latLng(item.latitude_start, item.longitude_start, { icon: customIcon }),
          // L.latLng(middleLocation.latitude, middleLocation.longitude, { icon: customIcon }),
          L.latLng(item.latitude_end, item.longitude_end, { icon: customIcon })
        ],
        
        routeWhileDragging: true,
        lineOptions: {
          styles: [
            {
              color: 'green',
              opacity: 0.6,
              weight: 4
            }
          ]
        },
        createMarker: function (waypointIndex, waypoint, numberOfWaypoints) {
          return L.marker(waypoint.latLng, { icon: customIcon });
        }
      }).addTo(map);

      routingControlRefs.current.push(routingControl);

    });

    const routingControl = L.Routing.control({
      waypoints: [
        L.latLng(startLocation.latitude, startLocation.longitude),
        L.latLng(middleLocation.latitude, middleLocation.longitude),
        L.latLng(endLocation.latitude, endLocation.longitude)
      ],
      routeWhileDragging: true,
      lineOptions: {
        styles: [
          {
            color: 'green',
            opacity: 0.6,
            weight: 4
          }
        ]
      },
      createMarker: function() { return null; }
    }).addTo(map);
    
    // routingControlRef.current = routingControl;
    routingControlRefs.current.push(routingControl);

    return () => {
      // if (routingControlRef.current) {
      //   routingControlRef.current.getPlan().setWaypoints([]);
      //   routingControlRef.current.hide();
      //   map.removeControl(routingControlRef.current);
      // }

      routingControlRefs.current.forEach((control) => {
        if (control) {
          control.getPlan().setWaypoints([]);
          control.hide();
          map.removeControl(control);
        }
      });
      

      // routingControlRefs.map((item) => (
      //   item.current.getPlan().setWaypoints([]),
      //   item.current.hide(),
      //   map.removeControl(item.current)
      // ))

      if (map && typeof map.remove === 'function') {
        map.remove();
      }
    };
  }, [phoneData, startLocation, middleLocation, endLocation]);

  return (
    <div style={{ height: '100vh', width: '100vw' }}>
      <div id="map" ref={mapRef} style={{ height: '100%', width: '100%' }} />
    </div>
  );
}

export default PhoneMap;
