import React, { useEffect } from 'react';
import { MapContainer, TileLayer, Marker } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import 'leaflet-routing-machine';

const RoadMap = () => {
  useEffect(() => {
    const startPoint = L.latLng(40.7128, -74.0060); // New York City
    const endPoint = L.latLng(34.0522, -118.2437); // Los Angeles

    // Create the map
    const map = L.map('map').setView(startPoint, 5);

    // Add the tile layer to the map
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Map data &copy; OpenStreetMap contributors'
    }).addTo(map);

    // Add the start and end markers to the map
    L.marker(startPoint).addTo(map);
    L.marker(endPoint).addTo(map);

    // Create a routing control and add it to the map
    L.Routing.control({
      waypoints: [
        startPoint,
        endPoint
      ],
      router: L.Routing.esri(),
      routeWhileDragging: true
    }).addTo(map);

    // Cleanup the map when the component is unmounted
    return () => map.remove();
  }, []);

  return (
    <div id="map" style={{ height: '400px' }}></div>
  );
};

export default RoadMap;
